const ChatRoom: React.VFC = (): JSX.Element => {

    const [uid, setUid] = useState<string>('')
    const [name, setName] = useState<string>('')
    const [items, setItems] = useState<UserData[]>([]);


    const {id} = useParams()
    const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        setItems([...items, {name: name, uid: uid}]);
      }

    return (
        <div>
            <h2>{id}</h2>
                <div className="balloon_l">
                {/* <img src={FemaleWorkerCheckingData} alt="https://stand-4u.com/stand-4u/wp-content/uploads/2019/09/s4man.png" > */}
