                <div className="balloon_r">

                    <table>
                        {items.map((item: UserData) => {
                            return(
                            <tr>
                                {/* <td>{item.name}</td> */}
                                <div className="balloon_r">
                                    <div className="says"><p><td>{item.uid}</td></p></div>
                                    <div className="faceicon">
                                        <img src={`${process.env.PUBLIC_URL}/hiroyuki.jpg`} alt="Logo" />
                                    </div>
                                </div>
                            </tr>
                            );
                            })}
                    </table>
                </div>
